CREATE OR REPLACE TRIGGER login_audit_eventAFTER LOGON ON DATABASEDECLARE  v_subject		VARCHAR2(100) := ‘User login audit event triggered’;  v_message		VARCHAR2(1000);BEGIN  INSERT INTO login_audit_table values(    Login_audit_seq.nextval,    Sysdate,    SYS_CONTEXT(‘USERENV’,’SESSION_USERID’),    SYS_CONTEXT(‘USERENV’,’IP_ADDRESS’),    SYS_CONTEXT(‘USERENV’,’HOST’));    v_message := ‘User ‘ || SYS_CONTEXT(‘USERENV’,’SESSION_USERID’) ||                  	‘ logged into the database at ‘ || sysdate || ‘ from host ‘ || SYS_CONTEXT(‘USERENV’,’HOST’);    SEND_email(‘DBA-GROUP@mycompany.com’,             	            V_subject,	            V_message);END;/